insert into t(id,dias,q) select t.idAluno,sum(DATEDIFF((select if(t.dataTermino is NULL,CURDATE(),t.dataTermino)),t.dataInicio)) as dias,count(t.idAluno) as q from j17_trancamentos as t group by t.idAluno;
